package com.kishorebabu.popularmovies;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import com.kishorebabu.popularmovies.model.Movie;import retrofit.Callback;import retrofit.Response;import retrofit.Retrofit;import timber.log.Timber;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        Timber.tag("MainActivity");        MovieDBApi movieDBApi = PopularMoviesApplication.getServiceSharedInstance();        movieDBApi.getMovieByID(293660, BuildConfig.TMDB_API_KEY).enqueue(new Callback<Movie>() {            @Override            public void onResponse(Response<Movie> response, Retrofit retrofit) {                if (response.isSuccess()) {                    Timber.v("Movie retrieved successfully.");                }            }            @Override            public void onFailure(Throwable t) {                Timber.e(t, "Failed to get the movie");            }        });    }}