package com.kishorebabu.popularmovies;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import timber.log.Timber;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        Timber.tag(MainActivity.class.getSimpleName());        if (findViewById(R.id.container_details_qweqwe) != null) {            if (savedInstanceState == null) {                getSupportFragmentManager().beginTransaction()                        .replace(R.id.container_details_qweqwe, new MovieDetailsFragment())                        .commit();            }        }    }}