package com.kishorebabu.popularmovies;import com.kishorebabu.popularmovies.model.Movie;import com.kishorebabu.popularmovies.model.MoviesList;import retrofit.Call;import retrofit.http.GET;import retrofit.http.Path;import retrofit.http.Query;/** * Created by Kishore on 2/28/2016. */public interface MovieDBApi {    @GET("movie/{movieID}")    Call<Movie> getMovieByID(@Path("movieID") int movieID, @Query("api_key") String apiKey);    @GET("discover/movie?sort_by=popularity.desc")    Call<MoviesList> getMostPopularMovies(@Query("api_key") String apiKey);    @GET("/discover/movie/?sort_by=vote_average.desc")    Call<MoviesList> getHighestRatedMovies(@Query("api_key") String apiKey);}