package com.kishorebabu.popularmovies;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.widget.TextView;import com.kishorebabu.popularmovies.model.Movie;/** * Created by Kishore on 2/28/2016. */public class MovieDetails extends AppCompatActivity {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_movie_details);        TextView movieName = (TextView) findViewById(R.id.movie_name);        Bundle bundle = getIntent().getExtras();        Movie movie = bundle.getParcelable(Constants.KEY_MOVIE);        if (movie != null)            movieName.setText(movie.getTitle());        else            movieName.setText(null);    }}