package com.kishorebabu.popularmovies;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.MenuItem;import timber.log.Timber;/** * Created by Kishore on 2/28/2016. */public class MovieDetails extends AppCompatActivity {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_movie_details);        Timber.tag(MovieDetails.class.getSimpleName());        if (savedInstanceState == null) {            MovieDetailsFragment details = new MovieDetailsFragment();            details.setArguments(getIntent().getExtras());            getSupportFragmentManager().beginTransaction()                    .replace(R.id.details_container, details)                    .commit();        }        if (getSupportActionBar() != null) {            getSupportActionBar().setHomeButtonEnabled(true);            getSupportActionBar().setDisplayHomeAsUpEnabled(true);        }    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        if (item.getItemId() == android.R.id.home) {            finish();        }        return super.onOptionsItemSelected(item);    }}