package com.kishorebabu.popularmovies;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v7.app.AppCompatActivity;import android.view.MenuItem;import android.widget.ImageView;import android.widget.TextView;import com.kishorebabu.popularmovies.model.Movie;import com.squareup.picasso.Picasso;import org.joda.time.DateTime;/** * Created by Kishore on 2/28/2016. */public class MovieDetails extends AppCompatActivity {    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_movie_details);        ImageView moviePoster = (ImageView) findViewById(R.id.movie_poster);        TextView movieReleaseYear = (TextView) findViewById(R.id.movie_release_year);        TextView movieLength = (TextView) findViewById(R.id.movie_length);        TextView movieRating = (TextView) findViewById(R.id.movie_rating);        TextView movieDesc = (TextView) findViewById(R.id.movie_desc);        Bundle bundle = getIntent().getExtras();        Movie movie = bundle.getParcelable(Constants.KEY_MOVIE);        if (movie != null) {            movieReleaseYear.setText(String.valueOf(DateTime.parse(movie.getReleaseDate()).getYear()));            movieLength.setText(movie.getRuntime() != null ? String.format("%smin", String.valueOf(movie.getRuntime())) : "");            movieRating.setText(String.format("%s/10", String.valueOf(movie.getVoteAverage())));            movieDesc.setText(movie.getOverview());            Picasso.with(this)                    .load(String.format("http://image.tmdb.org/t/p/w185/%s", movie.getPosterPath()))                    .into(moviePoster);            if (getSupportActionBar() != null) {                getSupportActionBar().setHomeButtonEnabled(true);                getSupportActionBar().setDisplayHomeAsUpEnabled(true);                getSupportActionBar().setTitle(movie.getTitle());            }        }    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        if (item.getItemId() == android.R.id.home) {            finish();        }        return super.onOptionsItemSelected(item);    }}